import React, {Component} from 'react';
import {connect} from "react-redux";
import {Table, Tag, Space, Form, Row, Col, Input, Button} from 'antd';
import {actionCreators} from "../community/store";
import {NavLink} from "react-router-dom";


const columns = [
    {
        title: '社团名称',
        dataIndex: 'name',
        key: 'id',
        render: text => <a>{text}</a>,
    },
    {
        title: '社团类型',
        dataIndex: 'name',
        key: 'type.id',
        render: text => <a>{text}</a>,
    },

    // {
    //     title: '社团类型',
    //     key: 'type',
    //     dataIndex: 'type',
    //     render: type => (
    //         <span>
    //     {type.map(type => {
    //         let color = type.length > 5 ? 'geekblue' : 'green';
    //         if (type === 'loser') {
    //             color = 'volcano';
    //         }
    //         return (
    //             <Tag color={color} key={type}>
    //                 {type.toUpperCase()}
    //             </Tag>
    //         );
    //     })}
    //   </span>
    //         )
    // },
    {
        title: '操作',
        key: 'id',
        render: () => (
            <Space size="middle">
                <a onClick={this.props.handleUpdate(this.key)}><NavLink to="/admin/community/updateCommunity">修改</NavLink></a>
                <a>删除</a>
            </Space>
        ),
    },
];


class Community extends Component {



    componentWillMount() {
        this.props.getCommunity();
        console.log(this.props.data)
    }


    render() {

        return(
            <div className="communityList">

                <Form

                    name="advanced_search"
                    className="ant-advanced-search-form"

                >
                    <Row gutter={24}>
                        <Col span={25} >
                            <Form.Item

                            >
                                <Input style={{ with: '500px' }}/>
                            </Form.Item>
                        </Col>
                    </Row>
                    {/*<Row>*/}
                    {/*    <Col span={10} style={{  }}>*/}
                    {/*        /!*<Button type="primary" htmlType="submit">*!/*/}
                    {/*        /!*    查询*!/*/}
                    {/*        /!*</Button>*!/*/}
                    {/*        /!*<Button*!/*/}
                    {/*        /!*    style={{ margin: '0 8px' }}*!/*/}
                    {/*        /!*    onClick={() => {*!/*/}

                    {/*        /!*    }}*!/*/}
                    {/*        /!*>*!/*/}
                    {/*        /!*    添加社团*!/*/}
                    {/*        /!*</Button>*!/*/}
                    {/*        <Button*/}
                    {/*            type="primary"*/}
                    {/*            htmlType="submit"*/}

                    {/*        >*/}
                    {/*           查询*/}
                    {/*        </Button>*/}

                    {/*    </Col>*/}
                    {/*</Row>*/}

                    <Row type="flex" justify="space-between" align="middle" style={{marginTop:10}}>
                        <Col><h2>文件列表</h2></Col>
                        <Col>
                            <Form layout="inline">
                                <Form.Item label={"文件名"} >
                                    <Input value={formData.get("search")} onChange={(e)=>{this.handleInputChange(e.target.value)}}
                                           onPressEnter={()=>{this.handleSearchSubmit()}} allowClear placeholder="winrar" />
                                </Form.Item>
                                <Form.Item style={{float:"right"}}>
                                    <Button  type="primary" style={{marginRight:"10px"}}>查找</Button>
                                    <Button  type="default" style={{marginRight:"10px"}}>刷新</Button>
                                    <Button  type="default">文件上传</Button>
                                </Form.Item>
                            </Form>
                        </Col>
                    </Row>
                </Form>

                <Table
                    columns={columns}
                    pagination={{ position: [ 'bottomCenter'] }}
                    dataSource={this.props.data}
                />

            </div>
        )
    }

}
const mapState = (state) => ({
    data: state.getIn(['community','data'])
});
const mapDispatch = (dispatch) => ({

    getCommunity(){
        dispatch(actionCreators.communityRequest())
    },
    handleUpdate(id){
        dispatch(actionCreators.updateCommunity(id))
    }

})

export default connect(mapState,mapDispatch)(Community);
