import { fromJS } from 'immutable';
import * as constants from './constants';

const defaultState = fromJS ({
    data:[],
    getState: false,
    communityId:'',
    fields: ''
});

const getCommunitySuccess =(state,action)=>{
    return state.merge({
        'getState': action.getState,
        'data': action.data
    });
}
const getCommunityError =(state,action)=>{
    return state.merge({
        'getState': action.getState,
    });
}
const updateCommunityById =(state,action)=>{
    return state.merge({
        'communityId': action.communityId
    });
}
const searchCommunityByFields =(state,action)=>{
    return state.merge({
        'fields': action.fields
    });
}

export default (state = defaultState,action) => {
    switch (action.type) {
        case constants.GET_COMMUNITY_SUCCESS:
            return getCommunitySuccess(state,action)
        case constants.GET_COMMUNITY_ERROR:
            return getCommunityError(state, action)
        case constants.UPDATE_COMMUNITY_ID:
            return updateCommunityById(state, action)
        case constants.SEARCH_COMMUNITY_FIELDS:
            return searchCommunityByFields(state, action)
        default:
            return state;
    }

}
