import axios from 'axios';
import * as constants from './constants';


const getCommunitySuccess = (result)=>({
    type : constants.GET_COMMUNITY_SUCCESS,
    data: result,
    getState: true
})

const getCommunityError = (error)=>({
    type : constants.GET_COMMUNITY_ERROR,
    message : error.message,
    getState: false
})
const updateCommunityById = (id)=>({
    type: constants.UPDATE_COMMUNITY_ID,
    communityId: id
})
const searchCommunitySuccess = (result)=>({
    type: constants.SEARCH_COMMUNITY_SUCCESS,
    data: result
})
const searchCommunityError = (error)=>({
    type: constants.SEARCH_COMMUNITY_ERROR,
    message : error.message
})
const addCommunityError = (error)=>({
    type: constants.ADD_COMMUNITY_ERROR,
    message : error.message
})
const addCommunitySuccess = (result)=>({
    type: constants.ADD_COMMUNITY_SUCCESS,
    visible: false,
})

export const communityRequest = () => {
    return (dispatch) => {
        axios.get('http://localhost:8080/admin/getCommunity').then((res) => {
            const result = res.data.data;
            const action = getCommunitySuccess(result);
            dispatch(action);
        }).catch(error => {
            const action = getCommunityError(error);
            dispatch(action);
        });
    }
};
export const updateCommunity  = (id) => {
    return (dispatch) => {
        const action = updateCommunityById(id);
        dispatch(action);
    }
};
export const searchCommunity  = (value) => {
    return (dispatch) => {
        axios.get('http://localhost:8080/admin/searchCommunity', {
            params: {
                fields: value
            }
        }).then((res) => {
            const result = res.data.data;
            const action = searchCommunitySuccess(result);
            dispatch(action);
        }).catch(error => {
            const action = searchCommunityError(error);
            dispatch(action);
        });
    }
};
export const addCommunity  = (values) => {
    return (dispatch) => {
        axios.get('http://localhost:8080/admin/addCommunity', values).then((res) => {
            const result = res.data.data;
            const action = addCommunitySuccess(result);
            dispatch(action);
        }).catch(error => {
            const action = searchCommunityError(error);
            dispatch(action);
        });
    }
};
