import React, {Component} from 'react';
import {connect} from "react-redux";
import { Table, Tag, Space } from 'antd';
import {actionCreators} from "../community/store";


const columns = [
    {
        title: '社团名称',
        dataIndex: 'name',
        key: 'name',
        render: text => <a>{text}</a>,
    },
    {
        title: 'age',
        dataIndex: 'age',
        key: 'age',
    },
    {
        title: 'Address',
        dataIndex: 'address',
        key: 'address',
    },
    {
        title: '社团类型',
        key: 'type',
        dataIndex: 'type',
        render: type => (
            <span>
        {type.map(type => {
            let color = type.length > 5 ? 'geekblue' : 'green';
            if (type === 'loser') {
                color = 'volcano';
            }
            return (
                <Tag color={color} key={type}>
                    {type.toUpperCase()}
                </Tag>
            );
        })}
      </span>
        ),
    },
    {
        title: '操作',
        key: 'manage',
        render: () => (
            <Space size="middle">
                <a>修改</a>
                <a>删除</a>
            </Space>
        ),
    },
];

