import React, {Component} from 'react';
import {connect} from "react-redux";
import {Table, Tag, Space, Form, Row, Col, Input, Button, Modal, Select, Card} from 'antd';
import {actionCreators} from "../community/store";
import {NavLink,Redirect} from "react-router-dom";
import {PlusSquareOutlined} from "@ant-design/icons";



const { Option } = Select;

class Community extends Component {


    componentWillMount() {
        this.props.getCommunity();
        console.log(this.props.data)
    }


    render() {

        const columns = [
            {
                title: '社团名称',
                dataIndex: 'name',
                key: 'id',
                render: text => <a>{text}</a>,
            },
            {
                title: '社团类型',
                dataIndex: 'type.name',
                key: 'type.id',
                render: text => <a>{text}</a>,
            },

            // {
            //     title: '社团类型',
            //     key: 'type',
            //     dataIndex: 'type',
            //     render: type => (
            //         <span>
            //     {type.map(type => {
            //         let color = type.length > 5 ? 'geekblue' : 'green';
            //         if (type === 'loser') {
            //             color = 'volcano';
            //         }
            //         return (
            //             <Tag color={color} key={type}>
            //                 {type.toUpperCase()}
            //             </Tag>
            //         );
            //     })}
            //   </span>
            //         )
            // },

            {

                title: '操作',
                key: 'id',
                render: (text,record,index) => (

                    <Space size="middle">
                        <a onClick={()=> handleUpdate(record.id)}><NavLink to="/admin/community/updateCommunity">修改</NavLink></a>
                        <a onClick={()=> handleDelete(record.id)}>删除</a>
                    </Space>

                ),
            },
        ];



        const {handleSearch,handleShowModal,handleAddCommunity,handleCancel,handleUpdate,handleDelete} = this.props;
        if(this.props.deleteState){return <Redirect to="/admin/community"/>}
        return(
            <div className="communityList">

                <Form

                    name="advanced_search"
                    className="ant-advanced-search-form"
                >
                    <Row type="flex" justify="space-between" align="middle" style={{marginTop:10}}>

                        <Col>
                            <Form layout="inline"  ref="search_form">
                                <Form.Item label={"社团名"}  name="fields">
                                    <Input  />
                                </Form.Item>
                                <Form.Item style={{float:"right"}}>
                                    <Button  type="primary" onClick={()=>handleSearch(this.refs.search_form.getFieldValue())} style={{marginRight:"10px"}}>查找</Button>
                                    <Button  type="default"  onClick={()=>handleShowModal()}><PlusSquareOutlined />添加社团</Button>

                                </Form.Item>
                            </Form>
                        </Col>
                    </Row>
                </Form>

                <Card>
                <Table
                    columns={columns}
                    pagination={{ pageSize:8, position: [ 'bottomCenter'] }}
                    dataSource={this.props.data}
                />
                </Card>

                <Modal
                    visible={this.props.visible}
                    title="添加社团"
                    onOk={()=>handleAddCommunity()}
                    onCancel={()=>handleCancel()}
                    footer={[
                        <Button key="back" onClick={()=>handleCancel()}>
                            取消
                        </Button>,
                        <Button key="submit" type="primary" loading={this.props.loading} onClick={()=>handleAddCommunity(this.refs.communityForm.getFieldValue())}>
                            确定
                        </Button>,

                    ]}
                >
                    <Form
                        name="addCommunity"
                        ref="communityForm"
                        scrollToFirstError
                    >
                        <Form.Item
                            name="name"
                            label="社团名称"
                            rules={[
                                {
                                    required: true,
                                    message: '请输入所要添加的社团的名字!',
                                },
                            ]}
                        >
                            <Input />
                        </Form.Item>

                        <Form.Item
                            name="description"
                            label="社团简介"
                            rules={[
                                {
                                    required: true,
                                    message: '请填写社团简介!',
                                },
                            ]}
                        >
                            <Input />
                        </Form.Item>


                        <Form.Item
                            name="type"
                            label="社团类型"
                            hasFeedback
                            rules={[
                                {
                                    required: true,
                                    message: '请选择社团类型!',
                                },
                            ]}
                        >
                            <Select placeholder="请选择社团类型">
                                <Option value="文体类">文体类</Option>
                                <Option value="艺术类">艺术类</Option>
                            </Select>
                        </Form.Item>
                    </Form>

                </Modal>

            </div>
        )
    }

}
const mapState = (state) => ({
    data: state.getIn(['community','data']),
    visible: state.getIn(['community','visible']),
    loading: state.getIn(['community','loading']),
    deleteState: state.getIn(['community','deleteState'])
});
const mapDispatch = (dispatch) => ({

    getCommunity(){
        dispatch(actionCreators.communityRequest())
    },
    handleUpdate(id){
        console.log(id)
        dispatch(actionCreators.updateCommunity(id))
    },
    handleSearch(value){
        console.log(value)
        dispatch(actionCreators.searchCommunity(value))
    },
    handleAddCommunity(values){
        console.log(values)
        dispatch(actionCreators.addCommunity(values))
    },
    handleShowModal(){
        console.log("show")
        dispatch(actionCreators.handleShow())
    },
    handleCancel(){
        dispatch(actionCreators.handleCancel())
    },
    handleDelete(id){
        console.log(id)
        dispatch(actionCreators.handleDelete(id))
    }

})

export default connect(mapState,mapDispatch)(Community);
