import React,{Component}from 'react';
import {Button, Card, Col, Form, Input, Modal, Row, Select, Space, Table, Tag} from "antd";
import {PlusSquareOutlined} from "@ant-design/icons";
import {actionCreators} from "../community/store";
import {Redirect} from "react-router-dom";
import axios from "axios";
import qs from "qs";

const { Option } = Select;
class User extends Component {

    state = {
        data: [],
        message: "",
        visible: false,
        loading:false,
        deleteState:false,
        updateVisible:false,
        updateState:false,
        updateId: 0
    };
    componentWillMount() {
        this.getUsers();
        console.log(this.state.data);
    }

    handleSearch(fields){
        axios.get('api/admin/searchUserByUsername',fields).then((res)=>{
            const result = res.data.data;
            this.state({
                data: result,
            })
        }).catch(error=>{
            this.setState({
                message: error
            })
        })
    };
    getUsers(){
        axios.get('/api/admin/getUser').then((res) => {
            const result = res.data.data;
            this.setState({
                data: result,
            })
        }).catch(error => {
            this.setState({
                message: error
            })
        });
    };
    handleShowModal(){
        this.setState({
            visible: true,
        })
    };
    handleCancel(){
        this.setState({
            visible: false,
        })
    };
    handleAddUser(value){
        axios.post('/api/admin/addUser', qs.stringify(value)).then((res =>{
            if (res.status === 200){
                this.setState({
                    visible: false,
                })
            }
        })).catch(error => {
            this.setState({
                message: error
            })
        })


    }



    render(){

        const columns = [
            {
                title: '成员名称',
                dataIndex: 'name',
                key: 'id',
                render: text => <a>{text}</a>,
            },
            {
                title: '所属院系',
                dataIndex:'department',
                key: 'id',
                render: (department) => (
                    <Tag color={"green"} key={department}>{department}</Tag>
                ),
            },

            {
                title: '联系电话',
                dataIndex:'phone',
                key: 'id',
                render: text => <a>{text}</a>,
            },

            {
                title: '年级',
                dataIndex:'grade',
                key: 'id',
                render: text => <a>{text}</a>,
            },

            {

                title: '操作',
                key: 'id',
                render: (text,record) => (

                    <Space size="middle">
                        <a onClick={()=> handleUpdate(record.id-1)}>修改</a>
                        <a onClick={()=> handleDelete(record.id)}>删除</a>
                    </Space>

                ),
            },
        ];



        const {handleSearch,handleShowModal,handleAddUser,updateId,handleUpdate,handleCancel,handleDelete,handleCancelUpdate,handleUpdateUser} = this;
        if(this.props.deleteState){return <Redirect to="/admin/user"/>}
        return(
            <div className="userList">

                <Card style={{width: 1240}}>
                    <Form

                        name="advanced_search"
                        className="ant-advanced-search-form"
                    >
                        <Row type="flex"  align="middle" style={{marginBottom:10}}>

                            <Col>
                                <Form layout="inline"  ref="searchForm">
                                    <Form.Item label={"用户名"}  name="fields">
                                        <Input  />
                                    </Form.Item>
                                    <Form.Item style={{float:"right"}}>
                                        <Button  type="primary" onClick={()=>handleSearch(this.refs.searchForm.getFieldValue())} style={{marginRight:"10px"}}>查找</Button>
                                        <Button  type="default"  onClick={()=>handleShowModal()}><PlusSquareOutlined />添加用户</Button>

                                    </Form.Item>
                                </Form>
                            </Col>
                        </Row>
                    </Form>


                    <Table
                        columns={columns}
                        pagination={{ pageSize:6, position: [ 'bottomCenter'] }}
                        dataSource={this.props.data}
                    />
                </Card>

                <Modal
                    visible={this.props.visible}
                    destroyOnClose={true}
                    title="添加社团"
                    onOk={()=>handleAddUser()}
                    onCancel={()=>handleCancel()}
                    footer={[
                        <Button key="back" onClick={()=>handleCancel()}>
                            取消
                        </Button>,
                        <Button key="submit" type="primary" loading={this.props.loading} onClick={()=>handleAddUser(this.refs.communityForm.getFieldValue())}>
                            确定
                        </Button>,

                    ]}
                >
                    <Form
                        name="addUser"
                        ref="userForm"
                        scrollToFirstError
                    >
                        <Form.Item
                            name="name"
                            label="用户名称"
                            rules={[
                                {
                                    required: true,
                                    message: '请输入所要添加的用户的名字!',
                                },
                            ]}
                        >
                            <Input />
                        </Form.Item>

                        <Form.Item
                            name="description"
                            label="用户简介"
                            rules={[
                                {
                                    required: true,
                                    message: '请填写社团简介!',
                                },
                            ]}
                        >
                            <Input />
                        </Form.Item>


                        <Form.Item
                            name="type"
                            label="社团类型"
                            hasFeedback
                            rules={[
                                {
                                    required: true,
                                    message: '请选择社团类型!',
                                },
                            ]}
                        >
                            <Select placeholder="请选择社团类型">
                                <Option value="文体类">文体类</Option>
                                <Option value="艺术类">艺术类</Option>
                            </Select>
                        </Form.Item>
                    </Form>
                </Modal>

                <Modal
                    visible={this.props.updateVisible}
                    destroyOnClose={true}
                    title="修改用户"
                    onOk={()=>handleUpdateCommunity()}
                    onCancel={()=>handleCancelUpdate()}
                    footer={[
                        <Button key="back" onClick={()=>handleCancelUpdate()}>
                            取消
                        </Button>,
                        <Button key="submit" type="primary" loading={this.props.loading} onClick={()=>handleUpdateUser(this.refs.communityForm.getFieldValue(),updateId)}>
                            确定
                        </Button>,

                    ]}
                >
                    <Form
                        name="updateCommunity"
                        ref="userForm"
                        scrollToFirstError
                    >
                        <Form.Item
                            name="name"
                            label="社团名称"
                            rules={[
                                {
                                    required: true,
                                    message: '请输入所要添加的社团的名字!',
                                },
                            ]}
                        >

                            {/*<Input defaultValue={this.props.data[updateId].name}/>*/}
                            <Input defaultValue="大学生心里社团"/>
                        </Form.Item>

                        <Form.Item
                            name="description"
                            label="社团简介"
                            rules={[
                                {
                                    required: true,
                                    message: '请填写社团简介!',
                                },
                            ]}
                        >
                            {/*<Input defaultValue={this.props.data[updateId].description}/>*/}
                            <Input defaultValue="我是大学生心里社团"/>
                        </Form.Item>


                        <Form.Item
                            name="type"
                            label="社团类型"
                            hasFeedback
                            rules={[
                                {
                                    required: true,
                                    message: '请选择社团类型!',
                                },
                            ]}
                        >
                            <Select placeholder="请选择社团类型" defaultValue="文体类">
                                <Option value="文体类">文体类</Option>
                                <Option value="艺术类">艺术类</Option>
                                <Option value="体育类">体育类</Option>
                            </Select>
                        </Form.Item>
                    </Form>
                </Modal>

            </div>
        )
    }
}
