import React, {Component} from 'react';
import {connect} from "react-redux";
import {Table, Tag, Space, Form, Row, Col, Input, Button, Modal, Select} from 'antd';
import {actionCreators} from "../community/store";
import {NavLink} from "react-router-dom";
import {PlusSquareOutlined} from "@ant-design/icons";


const columns = [
    {
        title: '社团名称',
        dataIndex: 'name',
        key: 'id',
        render: text => <a>{text}</a>,
    },
    {
        title: '社团类型',
        dataIndex: 'name',
        key: 'type.id',
        render: text => <a>{text}</a>,
    },

    // {
    //     title: '社团类型',
    //     key: 'type',
    //     dataIndex: 'type',
    //     render: type => (
    //         <span>
    //     {type.map(type => {
    //         let color = type.length > 5 ? 'geekblue' : 'green';
    //         if (type === 'loser') {
    //             color = 'volcano';
    //         }
    //         return (
    //             <Tag color={color} key={type}>
    //                 {type.toUpperCase()}
    //             </Tag>
    //         );
    //     })}
    //   </span>
    //         )
    // },
    {
        title: '操作',
        key: 'id',
        render: () => (
            <Space size="middle">
                <a onClick={this.props.handleUpdate(this.key)}><NavLink to="/admin/community/updateCommunity">修改</NavLink></a>
                <a>删除</a>
            </Space>
        ),
    },
];

const { Option } = Select;

class Community extends Component {


    // showModal = () => {
    //     this.setState({
    //         visible: true,
    //     });
    // };
    // handleOk = () => {
    //     this.setState({ loading: true });
    //     setTimeout(() => {
    //         this.setState({ loading: false, visible: false });
    //     }, 1000);
    // };

    handleCancel = () => {
        this.setState({ visible: false });
    };


    componentWillMount() {
        this.props.getCommunity();
        console.log(this.props.data)
    }


    render() {

        const {handleSearch,handleShowModal,handleAddCommunity,handleCancel} = this.props;
        return(
            <div className="communityList">

                <Form

                    name="advanced_search"
                    className="ant-advanced-search-form"
                >
                    <Row type="flex" justify="space-between" align="middle" style={{marginTop:10}}>

                        <Col>
                            <Form layout="inline"  ref="search_form">
                                <Form.Item label={"社团名"}  name="searchFields">
                                    <Input  />
                                </Form.Item>
                                <Form.Item style={{float:"right"}}>
                                    <Button  type="primary" onClick={()=>handleSearch(this.refs.search_form.getFieldValue())} style={{marginRight:"10px"}}>查找</Button>
                                    <Button  type="default"  onClick={()=>handleShowModal()}><PlusSquareOutlined />添加社团</Button>

                                </Form.Item>
                            </Form>
                        </Col>
                    </Row>
                </Form>

                <Table
                    columns={columns}
                    pagination={{ position: [ 'bottomCenter'] }}
                    dataSource={this.props.data}
                />

                <Modal
                    visible={this.props.visible}
                    title="添加社团"
                    onOk={()=>handleAddCommunity()}
                    onCancel={()=>handleCancel()}
                    footer={[
                        <Button key="back" onClick={()=>handleCancel()}>
                            取消
                        </Button>,
                        <Button key="submit" type="primary" loading={this.props.loading} onClick={()=>handleAddCommunity(this.refs.communityForm.getFieldValue())}>
                            确定
                        </Button>,

                    ]}
                >
                    <Form
                        name="addCommunity"
                        ref="communityForm"
                        scrollToFirstError
                    >
                        <Form.Item
                            name="name"
                            label="社团名称"
                            rules={[
                                {
                                    required: true,
                                    message: '请输入所要添加的社团的名字!',
                                },
                            ]}
                        >
                            <Input />
                        </Form.Item>

                        <Form.Item
                            name="description"
                            label="社团简介"
                            rules={[
                                {
                                    required: true,
                                    message: '请填写社团简介!',
                                },
                            ]}
                            hasFeedback
                        >
                            <Input />
                        </Form.Item>


                        <Form.Item
                            name="type"
                            label="社团类型"
                            hasFeedback
                            rules={[
                                {
                                    required: true,
                                    message: '请选择社团类型!',
                                },
                            ]}
                        >
                            <Select placeholder="请选择社团类型">
                                <Option value="文体类">文体类</Option>
                                <Option value="艺术类">艺术类</Option>
                            </Select>
                        </Form.Item>
                    </Form>

                </Modal>

            </div>
        )
    }

}
const mapState = (state) => ({
    data: state.getIn(['community','data']),
    visible: state.getIn(['community','visible']),
    loading: state.getIn(['community','loading'])
});
const mapDispatch = (dispatch) => ({

    getCommunity(){
        dispatch(actionCreators.communityRequest())
    },
    handleUpdate(id){
        console.log(id)
        dispatch(actionCreators.updateCommunity(id))
    },
    handleSearch(value){
        console.log(value)
        dispatch(actionCreators.searchCommunity(value))
    },
    handleAddCommunity(values){
        console.log(values)
        dispatch(actionCreators.addCommunity(values))
    },
    handleShowModal(){
        console.log("show")
        dispatch(actionCreators.handleShow())
    },
    handleCancel(){
        dispatch(actionCreators.handleCancel())
    }

})

export default connect(mapState,mapDispatch)(Community);
