import React from 'react';
import { Form, Input, Button, Checkbox,message,getFiledDecorator } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import './index.less';

const {FormItem} = Form.Item;
export default class  Login extends React.Component {

    handleSubmit = () =>{
        let userInfo = this.props.form.getFieldsValue();
        this.props.form.validateFields((err,values)=>{
            if(!err){
                message.success(`${userInfo.userName} 已通过`)
            }
        })
    }


    render() {
        const {getFiledDecorator} = this.props.form;
        return(
                <div className="form">
                {/*<Form*/}
                {/*    name="normal_login"*/}
                {/*    className="login-form"*/}
                {/*    initialValues={{ remember: true }}*/}

                {/*>*/}
                {/*    <Form.Item*/}
                {/*        name="用户名"*/}
                {/*        rules={[{ required: true, message: '请输入用户名!' }]}*/}
                {/*    >*/}
                {/*        <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Username" />*/}
                {/*    </Form.Item>*/}
                {/*    <Form.Item*/}
                {/*        name="密码"*/}
                {/*        rules={[{ required: true, message: '请输入密码!' }]}*/}
                {/*    >*/}
                {/*        <Input*/}
                {/*            prefix={<LockOutlined className="site-form-item-icon" />}*/}
                {/*            type="password"*/}
                {/*            placeholder="Password"*/}
                {/*        />*/}
                {/*    </Form.Item>*/}
                {/*    <Form.Item>*/}
                {/*        <Form.Item name="remember" valuePropName="checked" noStyle>*/}
                {/*            <Checkbox>记住我</Checkbox>*/}
                {/*        </Form.Item>*/}

                {/*        <a className="login-form-forgot" href="">*/}
                {/*            忘记密码*/}
                {/*        </a>*/}
                {/*    </Form.Item>*/}

                {/*    <Form.Item>*/}
                {/*        <Button type="primary" htmlType="submit" className="login-form-button" >*/}
                {/*            登陆*/}
                {/*        </Button>*/}
                {/*        Or <a href="">现在去注册!</a>*/}
                {/*    </Form.Item>*/}
                {/*</Form>*/}
                <Form>
                    <FormItem>
                        {
                            getFiledDecorator('userName',{
                                initialValue: '',
                                rules: [
                                    {
                                        required: true,
                                        message: '用户名不能为空'
                                    },
                                    {
                                        min:5,max:10,
                                        message: '长度不在范围内'
                                    }
                                ]
                            })(
                                <Input placeholder="请输入用户名"/>
                            )
                        }
                    </FormItem>
                    <FormItem>
                        {
                            getFiledDecorator('userPwd',{
                                initialValue: '',
                                rules: [
                                    {
                                        required: true,
                                        message: '密码不能为空'
                                    },
                                    {
                                        min:5,max:10,
                                        message: '长度不在范围内'
                                    }
                                ]
                            })(
                                <Input placeholder="请输入密码"/>
                            )
                        }
                    </FormItem>
                    <Form.Item>
                        <Button type="primary" htmlType="submit" className="login-form-button" >
                            登陆
                        </Button>
                        或者 <a href="/register">现在去注册!</a>
                    </Form.Item>
                </Form>
                </div>
        )
    }
}
